mapper.select:
select * from concert

    and concert_id = #{concertId}
    and theater_id = #{theaterId}
    and name like concat('%',#{name},'%')
    and low_price >= #{lowFee}
    and low_price <= #{highFee}
    and status = #{status}
    and location like concat('%',#{location},'%')
    and `describe` like concat('%',#{describe},'%')
    and type = #{type}
    and `player` like concat('%',#{player},'%')
    and is_selected = #{isSelected}


    and begin_time >= #{beginTime}
    and begin_time <= #{endTime}
