mapper.update:
UPDATE orders
user_id = #{userId}, address_book_id = #{addressBookId}, pay_status = #{payStatus}, pay_method = #{payMethod}, amount = #{amount}, remark = #{remark}, phone = #{phone}, address = #{address}, user_name = #{userName}, consignee = #{consignee}, cancel_reason = #{cancelReason}, rejection_reason = #{rejectionReason}, cancel_time = #{cancelTime}, begin_time = #{beginTime}, order_status = #{orderStatus}, create_time = #{createTime}, update_time = #{updateTime},
WHERE order_id = #{orderId}

mapper.select:
SELECT *
FROM orders


    order_id = #{orderId}
    AND user_id = #{userId}
    AND concert_id = #{concertId}
    AND user_name = #{userName}
    AND phone = #{phone}


    AND begin_time >= #{startTime}
    AND begin_time <= #{endTime}


    AND amount >= #{lowAmount}
    AND amount <= #{highAmount}


    AND pay_status = #{payStatus}
    AND pay_method = #{payMethod}
    AND order_status = #{orderStatus}
